const urlParams=new URLSearchParams(window.location.search),guestName=urlParams.get("to")||"Guest";function openInvitation(){document.getElementById("overlay").style.display="none",document.body.classList.remove("no-scroll"),document.getElementById("text-announcement").classList.remove("hidden"),document.getElementById("bottom-left").classList.remove("hidden"),document.getElementById("bottom-right").classList.remove("hidden"),document.getElementById("rsvp_btn").classList.remove("hidden")}document.getElementById("dearName").textContent=guestName,document.getElementById("name").value=guestName,window.onload=function(){window.scrollTo(0,0),document.body.classList.add("no-scroll"),document.getElementById("text-announcement").classList.add("hidden"),document.getElementById("bottom-left").classList.add("hidden"),document.getElementById("bottom-right").classList.add("hidden"),document.getElementById("rsvp_btn").classList.add("hidden")};
